package Notdefault;

public class ifElseBasic {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int score = 85;
		
		if(score >=90) {
			System.out.println("성적이 90 이상입니다.");
			System.out.println("등급이 A 입니다.");
		}
		else {
			System.out.println("성적이 90 이하입니다.");
			System.out.println("등급이 B 입니다.");
		}
	}

}
